import axios from "axios";
import React, { Component } from "react";


const Home = () => {
  const [search, setSearch] = React.useState()
  const [showSearch, setShowSearch] = React.useState(false)
  const [books, setBooks] = React.useState([])
  React.useEffect(()=>{
    if(search == null) return;
    axios.get(`http://localhost:8082/api/books/find?author${search}`).then(newBooks=> {

      if(newBooks.data == null || newBooks.data.books == null) return
      setBooks(newBooks.data.books)
    }).catch(e=>console.log(e))

  },[search]);

  const renderBooks = React.useCallback(()=>{
    if(showSearch == false) return


    return (
    <table className="">
    <thead>
      <tr> 
        <th scope="col">Title</th>
        <th scope="col">Author</th>
        <th scope="col">Source</th>
        <th scope="col">Y.O.P</th>
        <th scope="col">DOI</th>
        <th scope="col">Claimed Benifits</th>
      </tr>
    </thead>

    <tbody>

          {books.map((book)=>{
          return (
  <tr> 
  <td>{ book.title }</td>
  <td>{ book.isbn }</td>
  <td>{ book.source }</td>
  <td>{ book.publisher }</td>
  <td>{ book.published_date }</td>
  <td>{ book.description }</td>
</tr>
)
})}
    </tbody>
  </table>
 )
  },[
    search, books, showSearch
  ])

      return (
        <div >

        <form class="d-flex">
        <input class="form-control me-sm-2" type="text" value={search} onChange={(s)=>setSearch(s.target.value)} placeholder="Search An Article"/>


        <button class="btn btn-warning my-2 my-sm-0"  onClick={()=>setShowSearch(true)}>Search</button>
        {renderBooks()}
      </form>


          <br></br>
         <br></br>
         <br></br>
          <button onClick={() => {this.props.history.replace('/create-book')}} type="button" class="btn btn-info">Add New Article</button>
         <br></br>
         <br></br>
         <br></br>
         <button onClick={() => {this.props.history.replace('/Articles')}} type="button" class="btn btn-danger">Show All Available Articles</button>



        </div>
      );
  }
  export default Home;
